import { getLocalTasks, updateValues } from '../../services/localStorageService';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCircleArrowRight } from '@fortawesome/free-solid-svg-icons'
import { faTrash } from '@fortawesome/free-solid-svg-icons'
import './taskList.css';

// Component que renderiza as tasks registradas para uma data
function TaskList({ date }) {  
    const dataFormatada = ((date.getDate())) + "/" + ((date.getMonth() + 1)) + "/" + date.getFullYear();
    const tasks = getLocalTasks(date);


    const removeTask = (task) =>{
        updateValues(date, tasks.filter(taskItem => taskItem.taskValue != task.taskValue));    
    }
    
    const constructTasks = () => {
        const tasksElements = [];
        if (tasks)
            tasks.forEach(task => {
                tasksElements.push(
                    <div className='task-container'>
                        <FontAwesomeIcon icon={faCircleArrowRight} />
                        <text>
                            {task.taskValue}
                        </text>
                        <text>
                            {task.hourValue}
                        </text>
                        <button onClick={()=> { removeTask(task) } }>
                            <FontAwesomeIcon icon={faTrash} />
                        </button>
                    </div>
                )
            });
        else
            tasksElements.push(<div>Nehuma atividade planejada para hoje!</div>)

        return tasksElements;
    }
   
    return (
        <div className='tasks-container'>
            <text>Atividades para {dataFormatada} </text>
            {constructTasks()}
        </div>
    );
}

export default TaskList;
