import React from "react";
import Modal from 'react-modal';

function AddTask({ date, isVisible, onClose }) {
    const customStyles = {
        content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%'      
        },
    };

    return (
        <Modal
            style={customStyles}
            isOpen={isVisible}>
            <div className="modal-container">
                <button onClick={() => onClose()}>fechar</button>
                <div className="inputs-container">
                <input placeholder="Insira uma atividade."></input>
                <input placeholder="Insira a hora previsa para concluir"></input>
                </div>
   
            </div>
        </Modal>
    );
}

export default AddTask;
