import React from 'react';
import Calendar from 'react-calendar';
import { useState, useReducer } from 'react';
import 'react-calendar/dist/Calendar.css';
import TaskList from "../TaskList/taskList";
import AddTask from "../AddTask/addTask";
import './Main.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlusCircle } from '@fortawesome/free-solid-svg-icons'
import { getLocalTasks, updateValues } from '../../services/localStorageService';

// Component pai com todo o conteúdo que deve ser renderezido em tela
function Main() {
    const [daySelected, onChange] = useState(new Date());
    const [modalIsOpen, setIsOpen] = useState(false);
    const [, forceUpdate] = useReducer(x => x + 1, 0);

    function handleTasks() {
        forceUpdate();
    }
    const tasks = getLocalTasks(daySelected);
    const [stateTasks, setTask] = useStateWithCallbackLazy({ tasks });

    console.log(tasks)
    console.log(stateTasks);

    return (
        <div className="main-container">
            <div className="agenda-container">
                <text>BEM-VINDO A SUA AGENDA</text>
                <div className='calendar-container'>
                    <Calendar onChange={onChange} />
                </div>
            </div>
            <TaskList stateTasks={stateTasks} stateAction={(tasks) => setTask(tasks)} date={daySelected} />
            <div className='menu-container'>
                <button onClick={() => setIsOpen(true)}>
                    <FontAwesomeIcon icon={faPlusCircle} />
                </button>
            </div>
            <AddTask date={daySelected} isVisible={modalIsOpen} onClose={() => { setIsOpen(false); setTask(getLocalTasks(daySelected), () => handleTasks())}} />
        </div>
    );
}

export default Main;
