import React from "react";
import Modal from 'react-modal';

function AddTask({ date, isVisible, onClose }) {
    const customStyles = {
        content: {
          top: '50%',
          left: '50%',
          right: 'auto',
          bottom: 'auto',
          marginRight: '-50%',
          transform: 'translate(-50%, -50%)',
          display: 'table-row'
        },
      };

    return (
        <Modal
            style={customStyles}
            isOpen={isVisible}>
            <input placeholder="Insira uma atividade."></input>
            <input placeholder="Insira a hora previsa para concluir"></input>
            <button onClick={() => onClose()}>fechar</button>
        </Modal>
    );
}

export default AddTask;
