import React, { useEffect, useState } from "react";
import { addTasksToStorage, getLocalTasks } from '../../services/localStorageService';
import { TiLocationArrowOutline } from 'react-icons/ti'

function TaskList(date) {
    const constructTasks = (tasks) => {
        const tasksElements = [];
        if (tasks)
            tasks.items.forEach(task => {
                tasksElements.push(<div>
                    <TiLocationArrowOutline/>{task}
                </div>
                )
            });
        else
            tasksElements.push(<div>Não tem nada fera</div>)

        return tasksElements;
    }

    const tasks = getLocalTasks(date);
    return (
        <div>
            TASKS
            <div>
                {constructTasks(tasks)}
            </div>            
        </div>
    );
}

export default TaskList;
