export const addTasksToStorage = (date, tasks) => {
    const localResponse = localStorage.getItem(date.toString());

    if (localResponse)
        localStorage.removeItem(date.toString());

    localStorage.setItem(date.toString(), JSON.stringify(tasks));
}

export const getLocalTasks = (date) => {
    const localResponse = localStorage.getItem(date.toString());
    console.log(localResponse);
    if (localResponse)
        return JSON.parse(localResponse);

    return undefined;
}
